import React from 'react';
import WordsContainer from './WordsContainer';
const Resource = () => {
  return (
    <div className="resource">
      <div className="card">
        <div className="card-body">
          <div className="mt-3 progress">
            <div className="progress-bar bg-danger" role="progressbar" style={{width: `${10/3000}`}} aria-valuenow={25} aria-valuemin={0} aria-valuemax={100}>10%</div>
          </div>
          <h1 className="text-center">This is the title of the resouce.</h1>
          <img src="/placeholder.png" className="resource__main-image" />
          <p className="resource__description">This is some text within a card body.</p>
          <h3>Link to resource: www.google.com/this-name-of-the-article-that-this-links-to</h3>
          <WordsContainer />
        </div>
      </div>
    </div>
  )
}

export default Resource;
