import React, { useEffect } from 'react';
import { Plus } from 'react-bootstrap-icons';
import { useDispatch } from 'react-redux';
import { getWord} from '../../actions/words';

const WordButton = ({ word }) => {
  let fullWord;
  const dispatch = useDispatch();

  useEffect(async () => {
    fullWord = await dispatch(getWord(word.characterId));
  })

  if (!fullWord ) {
    return <div>Loading...</div>
  }

  return (
    <>
    <div className="word-button hover">
      <div 
      data-toggle="modal" data-target={`#${word._id}-modal`}
      className="badge badge-pill badge-primary mr-3 mt-3">
        <Plus size={24} />{word.simplified}
      </div>
    </div>
    <div class="modal fade" id={`${word._id}-modal`} tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
      <div class="modal-dialog" role="document">
        <div class="modal-content">
          <div class="modal-header">
            <h5 class="modal-title">{word.simplified}</h5>
            <button type="button" class="close" data-dismiss="modal" aria-label="Close">
              <span aria-hidden="true">&times;</span>
            </button>
          </div>
          <div class="modal-body">
            <h2>{word.pinyin}</h2>
            <h3>{word.english}</h3>
          </div>
          <div class="modal-footer">
            <button type="button" class="btn btn-secondary" data-dismiss="modal">Close</button>
            <button type="button" class="btn btn-primary">Save changes</button>
          </div>
        </div>
      </div>
    </div>
    </>
  )
}

export default WordButton;
