import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import AddWordButton from './AddWordButton';

const AddWordsSection = ({words, resource}) => {

  const renderWords = () => {
    console.log("WORDS")
    console.log(words)
    console.log("resource.words")
    console.log(resource.words)
    return words.map(word => {
  //CHECK TO SEE IF RESOURCE ALREADY HAS WORD
      let containsWord = resource.words.some(w => {
        return w.characterId === word._id;
      });
      if (containsWord) {
        return <div className="btn btn-danger">{word.simplified}</div>
      } else {
        return (
          <AddWordButton resource={resource} word={word} />
        )
      }
    })
  }

  if (!words) { return <div>Search for words...</div>}

  return (
    <div className="mt-2">
      <h3 className="text-center">Unique Words in this Resource</h3>
      {renderWords()}
    </div>
  )
}

export default AddWordsSection;
