import React from 'react';
import RemoveWordButton from './RemoveWordButton';


const WordsResourceHas = ({resource}) => {

  if (!resource) { return <div>Loading...</div>}
  const renderWords = () => {
    if (resource.words.length === 0 ) { return <div> No words </div> }
    return resource.words.map(word => {
      if (!word) { return <span></span>}
      return <RemoveWordButton resource={resource} word={word} />
    })
  }

  return (
    <div className="row">
      {renderWords()}
    </div>
  )
}

export default WordsResourceHas;
