import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { getResource, getWordsForResource } from '../../../actions/resources';
import { getWordBySimplified } from '../../../actions/words';
import WordsContainer from './WordsContainer';

const Edit = (props) => {
  const resourceId = props.match.params.id;
  const dispatch = useDispatch();
  const searchResult = useSelector(state => state.searchResult)
  const [searchTerm, setSearchTerm] = useState("");
  const [characterId, setChracterId] = useState("");
  const [words, setWords] = useState([]);

  useEffect( async() => {
    dispatch(getResource(resourceId));
    setWords(await dispatch(getWordsForResource(resourceId)));
  }, [searchResult]);

  const resource = useSelector(state => state.resource)

  const renderWords = () => {
    return resource.words.map(word => {
      return word;
    });
  }

  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch(getWordBySimplified(searchTerm));
  }

  const addWord = () => {
    console.log(searchResult[0]._id)
  }


  const renderSearchResult = () => {
    if (!searchResult) {
      return <div>Make a search...</div>
    } else if (searchResult.length === 0) {
      return <div>No result found</div>
    } else {
      return (
        <>
         <div>{searchResult[0].simplified}</div>
         <div onClick={addWord} className="ml-3 btn btn-primary"> Add</div>
        </>
      )
    }
  }  

  if (!resource || !words) {return <div>Loading...</div>}

  return (
    <div>
      <h1 className="text-center">Edit Words for {resource.title} </h1>
      <Link to={`/resources/${resourceId}/edit`}>{`< Back to resource`}</Link>
      <div className="row">
          <div className="col-6">
            <h2 className="text-center">Current Words</h2> 
          </div>
          <form className="add-word-search-form" onSubmit={handleSubmit}>
            <div className="col-12">
              <input value={searchTerm} onChange={(e) => setSearchTerm(e.target.value)} placeholder="Search..." className="form-control" name="searchTerm" />
            </div>
            <div className="col-1">
              <input type="submit" className="mt-3 mb-3 btn btn-primary" value="Search" />
            </div>
          </form>

          <div className="col-2">
            {renderSearchResult()}
          </div>
      </div>
      <WordsContainer words={words} />
      <hr/>
    </div>
  )
}

export default Edit;
