import { GET_RESOURCE, UPDATE_RESOURCE } from '../types/index';

export default (resource = null, action) => {
  switch (action.type) {
    case GET_RESOURCE:
    case UPDATE_RESOURCE:
      return action.payload;
    default:
      return resource;
  }
}
