import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getWord} from '../../actions/words';

const WordButton = ({ word }) => {
  const knownWords = useSelector(state => state.knownWords)
  const [fullWord, setFullWord] = useState(null);
  const dispatch = useDispatch();

  useEffect(async () => {
    setFullWord(await dispatch(getWord(word.characterId)));
  }, [])

  if (!fullWord ) {
    return <div>Loading...</div>
  }

  const wordIsKnown = () => {
    console.log(word.characterId)
    return knownWords.some(knownWord => {
      return knownWord._id === word.characterId
    })
  }

  if (wordIsKnown()) {
    return <li>KNOWN</li>
  }

  return (
    <>
      <button type="button" class="btn btn-primary badge badge-pill mr-2 ml-2" data-toggle="modal" data-target={`#${fullWord.simplified}`}>
        {fullWord.simplified}
      </button>

      <div class="modal fade" id={`${fullWord.simplified}`} tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
        <div class="modal-dialog" role="document">
          <div class="modal-content">
            <div class="modal-header">
              <h5 class="modal-title" id="exampleModalLabel">{fullWord.simplified}</h5>
              <button type="button" class="close" data-dismiss="modal" aria-label="Close">
                <span aria-hidden="true">&times;</span>
              </button>
            </div>
            <div class="modal-body">
              <p>{fullWord.pinyin}</p>
              <p>{fullWord.english}</p>
            </div>
              <div className="row">
                <div className="col-6">
                  <button type="button" class="btn btn-block btn-lg btn-secondary" data-dismiss="modal">Close</button>
                </div>
                <div className="col-6">
                  <button type="button" class="btn btn-block btn-lg btn-primary">Add word to list</button>
                </div>
              </div>
          </div>
        </div>
      </div>
    </>
  )
}

export default WordButton;
