import * as api from '../api';
import { GET_RESOURCES, 
  GET_RESOURCE, 
  CREATE_RESOURCE, 
  DELETE_RESOURCE, 
  UPDATE_RESOURCE} from '../types/index';

export const createResource = (resourceParams) => async (dispatch) => {
  try {
    const { data } = await api.createResource(resourceParams);
    dispatch({
      type: CREATE_RESOURCE,
      payload: data
    });
  } catch (error) {
    console.log(error);
  }
}

export const getResource = (resourceId) => async (dispatch) => {
  try {
    const { data } = await api.getResource(resourceId);
    dispatch({
      type: GET_RESOURCE,
      payload: data
    })
  } catch (error) {
    console.log(error);
  }
}

export const getResources = () => async (dispatch) => {
  try {
    const { data } = await api.getResources();
    console.log(data);
    dispatch({
      type: GET_RESOURCES,
      payload: data
    })
  } catch (error) {
    console.log(error);
  }
}
