import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { getResource } from '../../../actions/resources';
import AddItem from './AddItem';

const Edit = (props) => {
  const resourceId = props.match.params.id;
  const dispatch = useDispatch();
  const [searchTerm, setSearchTerm] = useState("")

  useEffect(() => {
    dispatch(getResource(resourceId));
  });

  const resource = useSelector(state => state.resource)

  const renderWords = () => {
    return resource.words.map(word => {
      return word;
    })
  }

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(searchTerm)
  }

  // DELETE ME 

  const item = {
    simplified: "洗手間",
    id: "123"
  }

  if (!resource) {return <div>Loading...</div>}

  return (
    <div>
      <h1 className="text-center">Edit Words for {resource.title} </h1>
      <Link to={`/resources/${resourceId}/edit`}>{`< Back to resource`}</Link>
      <div className="row">
          <div className="col-6">
            <h2 className="text-center">Current Words</h2> 
          </div>
          <form className="add-word-search-form" onSubmit={handleSubmit}>
            <div className="col-12">
              <input value={searchTerm} onChange={(e) => setSearchTerm(e.target.value)} placeholder="Search..." className="form-control" name="searchTerm" />
            </div>
            <div className="col-1">
              <input type="submit" className="mt-3 mb-3 btn btn-primary" value="Search" />
            </div>
          </form>

          <div className="col-2">
            <AddItem item={item} />
          </div>
      </div>
      <div className="jumbotron">{renderWords()}</div>
      <hr/>
    </div>
  )
}

export default Edit;
