import React, {useEffect} from 'react';
import WordsContainer from './WordsContainer';
import BackLink from '../shared/BackLink';
import { useDispatch, useSelector} from 'react-redux';
import { getResource } from '../../actions/resources';
import { Pencil } from 'react-bootstrap-icons';

const Resource = (props) => {
  const dispatch = useDispatch();
  const resourceId = props.match.params.id;
  const resource = useSelector(state => state.resource)
  useEffect(() => {
    dispatch(getResource(resourceId));
  }, [])
  console.log(resource)


  if (!resource) {
    dispatch(getResource(resourceId));
    console.log(resource)
    return <div>Loading...</div>

  }
  return (
    <div>
      <BackLink url="/resources" text="< Back to resources" />
      <div className="resource">
        <div className="card">
          <div className="card-body">
            <div className="mt-3 progress">
              <div className="progress-bar bg-danger" role="progressbar" style={{width: `${10/3000}`}} aria-valuenow={25} aria-valuemin={0} aria-valuemax={100}>10%</div>
            </div>
            <h1 className="text-center">{resource.title}
            <Pencil size={32} />
            </h1> 
            <img src={resource.image} className="resource__main-image" />
            <p className="resource__description">{resource.description}</p>
            <h3>{resource.link}</h3>
            <WordsContainer />
          </div>
        </div>
      </div>
    </div>
  )
}

export default Resource;
