import React from 'react';

const RecentWordItem = ({ word }) => {
  if (!word) { return <div>Loading...</div>}
  return (
    <li className="badge badge-primary badge-lg badge-pill">
      { word.simplified }
    </li>
  )
}

export default RecentWordItem;
