import React, { useEffect, useState } from 'react';
import { Plus } from 'react-bootstrap-icons';
import { useDispatch } from 'react-redux';
import { getWord} from '../../actions/words';

const WordButton = ({ word }) => {
  const [fullWord, setFullWord] = useState(null);
  const dispatch = useDispatch();

  useEffect(async () => {
    console.log("GET FULL WORD")
    setFullWord(await dispatch(getWord(word.characterId)));
  }, [])

  if (!fullWord ) {
    return <div>Loading...</div>
  }

  return (
    <>
    <div className="word-button hover">
      <div 
      data-toggle="modal" data-target={`#${fullWord._id}-modal`}
      className="badge badge-pill badge-primary mr-3 mt-3">
        <Plus size={24} />{fullWord.simplified}
      </div>
    </div>
    <div class="modal fade" id={`${fullWord._id}-modal`} tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
      <div class="modal-dialog" role="document">
        <div class="modal-content">
          <div class="modal-header">
            <h5>{fullWord.simplified}</h5>
            <button type="button" class="close" data-dismiss="modal" aria-label="Close">
              <span aria-hidden="true">&times;</span>
            </button>
          </div>

          <div class="modal-body">
            <p>{fullWord.pinyin}</p>
            <p>{fullWord.english}</p>
          </div>
          <div>
            <div className="row">
              <div className="col-6">
                <button type="button" class="btn btn-block btn-lg btn-secondary" data-dismiss="modal">Close</button>
              </div>
              <div className="col-6">
                <button type="button" class="btn btn-block btn-lg btn-primary">Add to list</button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
    </>
  )
}

export default WordButton;
