import React from 'react';
import { useDispatch } from 'react-redux';
import { addWordToResource } from '../../../actions/resources';

const AddWordButton = ({ word, resource }) => {
  const dispatch = useDispatch();
  const handleClick = () => {
    dispatch(addWordToResource(resource._id, word._id));
  }
  let alreadyContainsWord = resource.words.some(oldWord => {
    return oldWord._id === word._id
  });
  if (alreadyContainsWord) {
    return <div className="btn btn-danger">{word.simplified}</div>
  }
  return (
    <div onClick={handleClick} className="mr-2 word-to-add btn btn-primary">{word.simplified}</div>
  )
}

export default AddWordButton;
