import React from 'react';
import { useDisptach } from 'react-redux';
import { addWordToResource } from '../../../actions/resources';

const AddWordsSection = ({words}) => {

  const renderWords = () => {
    return words.map(word => {
      return (
        <div className="mr-2 word-to-add btn btn-primary">{word.simplified}</div>
      )
    })
  }

  if (!words) { return <div>Search for words...</div>}

  return (
    <div className="mt-2">
      <h3 className="text-center">Unique Words in this Resource</h3>
      {renderWords()}
    </div>
  )
}

export default AddWordsSection;
