import React, { useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { getResource } from '../../../actions/resources';
import AddItem from './AddItem';

const Edit = (props) => {
  const resourceId = props.match.params.id;
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(getResource(resourceId));
  });

  const resource = useSelector(state => state.resource)

  const renderWords = () => {
    return resource.words.map(word => {
      return word;
    })
  }


  return (
    <div>
      <h1 className="text-center">Edit Words for {resource.title} </h1>
      <Link to={`/resources/${resourceId}/edit`}>{`< Back to resource`}</Link>
      <div className="row">
        <div className="col-6">
          <h2 className="text-center">Current Words</h2> 
        </div>
        <div className="col-1">
          <input placeholder="Search..." className="form-control" name="searchTerm" />
        </div>
        <div className="col-1">
          <input type="submit" className="btn btn-primary" value="Search" />
        </div>
        <div className="col-2">
          <AddItem />
        </div>
      </div>
      <div className="jumbotron">{renderWords()}</div>
      <hr/>
    </div>
  )
}

export default Edit;
